.btn {
  margin: 10px;
  padding: 15px 45px;
  text-align: center;
  text-transform: uppercase;
  transition: 0.5s;
  background-size: 200% auto;
  color: white;
  box-shadow: 0 0 10px rgba(238, 238, 238, 0.3137254902);
  border-radius: 10px;
  display: block;
  text-decoration: none;
}

.btn:hover {
  background-position: right center;
  color: #fff;
  text-decoration: none;
}

