@media (min-width: 1200px) {
  .container {
    width: 80%;
  }
  #ctitle {
    font-size: 6rem;
  }
  #ctext {
    font-size: 3em;
  }
  .stepimg {
    width: 70rem;
  }
  #recruit {
    font-size: 1.1rem;
    width: 50%;
  }
}
@media (min-width: 992px) {
  .container {
    width: 80%;
  }
  .mockup {
    width: 14rem;
    float: right;
    position: bottom;
  }
  #ctitle {
    font-size: 4rem;
  }
  #ctext {
    font-size: 2rem;
  }
  .stepimg {
    width: 50rem;
  }
}
@media (min-width: 768px) {
  .container {
    width: 900%;
  }
  #ctitle {
    font-size: 3rem;
  }
  #ctext {
    font-size: 1.5rem;
  }
  .stepimg {
    width: 40rem;
  }
}
@media (min-width: 576px) {
  .container {
    width: 100%;
  }
  .stepimg {
    width: 30rem;
  }
}

